import React from 'react';
import Button from '../Button/Button';
import './ControlPanel.css';

const ControlPanel = (props) => {

    const {
        state,
        handleChange,
        handleAddClick,
        handleRemoveClick,
        handleSelectClick } = props;

    return (
        <div className='controlPanel' >
            <input value={state.inputValue} onChange={(e) => handleChange(e)} />
            <Button clickHandler={handleAddClick} label='Add' color='success'/>
            <Button clickHandler={handleRemoveClick} label='Remove selected' />
            <Button clickHandler={handleSelectClick } label={state.allSelected ? 'Unselect all' : 'Select All'} />
        </div>
    )
}

export default ControlPanel;
