import React from 'react';
import Button from '../Button/Button';
import './ControlPanel.css';
import { InputGroup, InputGroupAddon, Input, ButtonGroup } from 'reactstrap';

const ControlPanel = (props) => {

    const {
        state,
        handleChange,
        handleAddClick,
        handleRemoveClick,
        handleSelectClick } = props;

    return (
        <div className='controlPanel' >
            <InputGroup className='inputGroup'>
                <InputGroupAddon addonType="prepend">TODO:</InputGroupAddon>
                <Input value={state.inputValue} onChange={(e) => handleChange(e)} />
            </InputGroup>
            <div className='buttonGroup'>
                <Button 
                    clickHandler={handleAddClick} 
                    label='Add' 
                    color='success'
                />
                <Button 
                    clickHandler={handleRemoveClick} 
                    label='Remove selected' 
                    color='danger' 
                />
                <Button 
                    clickHandler={handleSelectClick } 
                    label={state.allSelected ? 'Unselect all' : 'Select All'} 
                    color='warning'    
                />
            </div>
        </div>
    )
}

export default ControlPanel;
